        GIVEN_IN,
        GIVEN_OUT
    }

    struct AccountLiquidity {
        int256 liquidity;
        uint256 collateral;
        uint256 debt;
    }

    struct VarsLiquidity {
        IPriceOracle oracle;
        address collateral;
        uint price;
        address[] _accountPools;
    }

