        uint amountPlusFeeUSD;
        uint _borrowCapacity;
        address[] tokens;
        uint[] prices;
    }

    struct Vars_Burn {
        uint amountUSD;
        uint debt;
        address[] tokens;
        uint[] prices;
    }

