        AccountLiquidity liq;
        Collateral collateral;
        uint ltv;
        address[] tokens;
        uint[] prices;
        uint amountUSD;
        uint debtUSD;
        uint amountOut;
        uint penalty;
        uint refundOut;
    }
}

